import React, { useState, useEffect } from 'react';
import { swapsAPI } from '../services/api';
import { useAuth } from '../hooks/useAuth';

const Swaps = () => {
  const [swaps, setSwaps] = useState([]);
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();

  useEffect(() => {
    fetchSwaps();
  }, []);

  const fetchSwaps = async () => {
    setLoading(true);
    try {
      const response = await swapsAPI.getSwaps();
      setSwaps(response.data);
    } catch (error) {
      console.error('Error fetching swaps:', error);
    }
    setLoading(false);
  };

  const updateSwapStatus = async (swapId, status) => {
    try {
      await swapsAPI.updateSwapStatus(swapId, status);
      fetchSwaps();
    } catch (error) {
      alert('Error updating swap status');
    }
  };

  const sentRequests = swaps.filter(swap => swap.sender_id === user.id);
  const receivedRequests = swaps.filter(swap => swap.receiver_id === user.id);

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'accepted': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return <div className="text-center py-8">Loading...</div>;
  }

  return (
    <div className="max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Skill Swap Requests</h1>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Received Requests */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4 text-blue-600">Received Requests</h2>
          {receivedRequests.length === 0 ? (
            <p className="text-gray-500">No requests received yet</p>
          ) : (
            <div className="space-y-4">
              {receivedRequests.map((swap) => (
                <div key={swap.id} className="border rounded-lg p-4">
                  <div className="flex items-center mb-3">
                    <div className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center">
                      {swap.sender_name.charAt(0).toUpperCase()}
                    </div>
                    <div className="ml-3">
                      <h3 className="font-semibold">{swap.sender_name}</h3>
                      <span className={`px-2 py-1 rounded-full text-xs ${getStatusColor(swap.status)}`}>
                        {swap.status.toUpperCase()}
                      </span>
                    </div>
                  </div>

                  <div className="mb-3">
                    <p className="text-sm">
                      <span className="font-medium">Offering:</span> {swap.skill_offered}
                    </p>
                    <p className="text-sm">
                      <span className="font-medium">Wants:</span> {swap.skill_requested}
                    </p>
                    {swap.message && (
                      <p className="text-sm mt-2">
                        <span className="font-medium">Message:</span> {swap.message}
                      </p>
                    )}
                  </div>

                  {swap.status === 'pending' && (
                    <div className="flex gap-2">
                      <button
                        onClick={() => updateSwapStatus(swap.id, 'accepted')}
                        className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 text-sm"
                      >
                        Accept
                      </button>
                      <button
                        onClick={() => updateSwapStatus(swap.id, 'rejected')}
                        className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 text-sm"
                      >
                        Reject
                      </button>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Sent Requests */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4 text-green-600">Sent Requests</h2>
          {sentRequests.length === 0 ? (
            <p className="text-gray-500">No requests sent yet</p>
          ) : (
            <div className="space-y-4">
              {sentRequests.map((swap) => (
                <div key={swap.id} className="border rounded-lg p-4">
                  <div className="flex items-center mb-3">
                    <div className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center">
                      {swap.receiver_name.charAt(0).toUpperCase()}
                    </div>
                    <div className="ml-3">
                      <h3 className="font-semibold">{swap.receiver_name}</h3>
                      <span className={`px-2 py-1 rounded-full text-xs ${getStatusColor(swap.status)}`}>
                        {swap.status.toUpperCase()}
                      </span>
                    </div>
                  </div>

                  <div className="mb-3">
                    <p className="text-sm">
                      <span className="font-medium">You offered:</span> {swap.skill_offered}
                    </p>
                    <p className="text-sm">
                      <span className="font-medium">You requested:</span> {swap.skill_requested}
                    </p>
                    {swap.message && (
                      <p className="text-sm mt-2">
                        <span className="font-medium">Your message:</span> {swap.message}
                      </p>
                    )}
                  </div>

                  <p className="text-xs text-gray-500">
                    Sent on {new Date(swap.created_at).toLocaleDateString()}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Swaps;
