import React, { useState, useEffect } from 'react';
import { usersAPI, swapsAPI } from '../services/api';

const Browse = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [location, setLocation] = useState('');
  const [loading, setLoading] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [swapRequest, setSwapRequest] = useState({
    skill_offered: '',
    skill_requested: '',
    message: ''
  });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await usersAPI.searchUsers({ skill: searchTerm, location });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
    setLoading(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchUsers();
  };

  const sendSwapRequest = async (e) => {
    e.preventDefault();
    try {
      await swapsAPI.sendSwapRequest({
        receiver_id: selectedUser.id,
        ...swapRequest
      });
      alert('Swap request sent successfully!');
      setSelectedUser(null);
      setSwapRequest({ skill_offered: '', skill_requested: '', message: '' });
    } catch (error) {
      alert('Error sending swap request');
    }
  };

  return (
    <div className="max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Browse Skills</h1>

      {/* Search Form */}
      <form onSubmit={handleSearch} className="bg-white p-6 rounded-lg shadow-md mb-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-sm font-medium mb-2">Search Skills</label>
            <input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="e.g., JavaScript, Guitar, Cooking"
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-primary"
            />
          </div>
          <div>
            <label className="block text-sm font-medium mb-2">Location</label>
            <input
              type="text"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              placeholder="e.g., New York, Remote"
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-primary"
            />
          </div>
          <div className="flex items-end">
            <button
              type="submit"
              className="w-full bg-primary text-white py-2 px-4 rounded-lg hover:bg-blue-600"
            >
              Search
            </button>
          </div>
        </div>
      </form>

      {/* Users Grid */}
      {loading ? (
        <div className="text-center py-8">Loading...</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {users.map((user) => (
            <div key={user.id} className="bg-white p-6 rounded-lg shadow-md">
              <div className="flex items-center mb-4">
                <div className="w-12 h-12 bg-gray-300 rounded-full flex items-center justify-center">
                  {user.name.charAt(0).toUpperCase()}
                </div>
                <div className="ml-3">
                  <h3 className="font-semibold">{user.name}</h3>
                  <p className="text-gray-600 text-sm">{user.location}</p>
                </div>
              </div>

              <div className="mb-4">
                <p className="text-sm text-gray-600 mb-2">Skills:</p>
                <div className="flex flex-wrap gap-1">
                  {user.skills && user.skills.map((skill, index) => (
                    <span key={index} className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded">
                      {skill}
                    </span>
                  ))}
                </div>
              </div>

              <div className="mb-4">
                <p className="text-sm text-gray-600">Availability: {user.availability}</p>
              </div>

              <button
                onClick={() => setSelectedUser(user)}
                className="w-full bg-secondary text-white py-2 px-4 rounded-lg hover:bg-green-600"
              >
                Request Swap
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Swap Request Modal */}
      {selectedUser && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white p-6 rounded-lg max-w-md w-full">
            <h3 className="text-xl font-bold mb-4">Request Skill Swap with {selectedUser.name}</h3>

            <form onSubmit={sendSwapRequest}>
              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Skill You're Offering</label>
                <input
                  type="text"
                  value={swapRequest.skill_offered}
                  onChange={(e) => setSwapRequest({...swapRequest, skill_offered: e.target.value})}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-primary"
                  required
                />
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Skill You Want</label>
                <input
                  type="text"
                  value={swapRequest.skill_requested}
                  onChange={(e) => setSwapRequest({...swapRequest, skill_requested: e.target.value})}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-primary"
                  required
                />
              </div>

              <div className="mb-6">
                <label className="block text-sm font-medium mb-2">Message</label>
                <textarea
                  value={swapRequest.message}
                  onChange={(e) => setSwapRequest({...swapRequest, message: e.target.value})}
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-primary"
                  rows="3"
                  placeholder="Tell them why you'd like to swap skills..."
                />
              </div>

              <div className="flex gap-4">
                <button
                  type="button"
                  onClick={() => setSelectedUser(null)}
                  className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded-lg hover:bg-gray-400"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="flex-1 bg-primary text-white py-2 px-4 rounded-lg hover:bg-blue-600"
                >
                  Send Request
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Browse;
