import React, { useState, useEffect } from 'react';
import { usersAPI } from '../services/api';
import { useAuth } from '../hooks/useAuth';

const Profile = () => {
  const { user, logout } = useAuth();
  const [profile, setProfile] = useState({
    name: '',
    email: '',
    location: '',
    availability: ''
  });
  const [editing, setEditing] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await usersAPI.getProfile();
      setProfile(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await usersAPI.updateProfile(profile);
      setEditing(false);
      alert('Profile updated successfully!');
    } catch (error) {
      alert('Error updating profile');
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen px-4 py-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-12 animate-fadeInUp">
          <h1 className="text-4xl font-bold font-poppins bg-gradient-to-r from-indigo-400 via-purple-500 to-pink-500 bg-clip-text text-transparent mb-4">
            My Profile
          </h1>
          <p className="text-slate-400 text-lg font-inter">Manage your account and preferences</p>
        </div>

        <div className="glass-card rounded-3xl p-8 animate-fadeInUp">
          {/* Profile Header */}
          <div className="flex flex-col md:flex-row items-center mb-8">
            <div className="avatar w-24 h-24 rounded-full text-3xl font-bold mb-4 md:mb-0 md:mr-6">
              {profile.name.charAt(0).toUpperCase()}
            </div>
            <div className="text-center md:text-left flex-1">
              <h2 className="text-2xl font-semibold text-white font-poppins mb-2">{profile.name}</h2>
              <p className="text-slate-400 font-inter mb-2 flex items-center justify-center md:justify-start">
                📧 {profile.email}
              </p>
              <div className="flex flex-wrap gap-2 justify-center md:justify-start">
                <span className="skill-tag px-3 py-1 rounded-full text-xs">
                  🌟 Active Member
                </span>
                <span className="skill-tag px-3 py-1 rounded-full text-xs">
                  🔥 Skill Swapper
                </span>
              </div>
            </div>
          </div>

          {editing ? (
            /* Edit Mode */
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-slate-300 text-sm font-medium mb-2 font-inter">
                    Full Name
                  </label>
                  <input
                    type="text"
                    value={profile.name}
                    onChange={(e) => setProfile({...profile, name: e.target.value})}
                    className="input-glass w-full px-4 py-3 rounded-2xl focus:ring-2 focus:ring-purple-500/50 transition-all duration-300"
                    required
                  />
                </div>

                <div>
                  <label className="block text-slate-300 text-sm font-medium mb-2 font-inter">
                    Email Address
                  </label>
                  <input
                    type="email"
                    value={profile.email}
                    className="input-glass w-full px-4 py-3 rounded-2xl bg-white/5 cursor-not-allowed opacity-60"
                    disabled
                  />
                  <p className="text-xs text-slate-500 mt-1 font-inter">Email cannot be changed</p>
                </div>
              </div>

              <div>
                <label className="block text-slate-300 text-sm font-medium mb-2 font-inter">
                  📍 Location
                </label>
                <input
                  type="text"
                  value={profile.location || ''}
                  onChange={(e) => setProfile({...profile, location: e.target.value})}
                  placeholder="e.g., New York, NY or Remote"
                  className="input-glass w-full px-4 py-3 rounded-2xl focus:ring-2 focus:ring-purple-500/50 transition-all duration-300"
                />
              </div>

              <div>
                <label className="block text-slate-300 text-sm font-medium mb-2 font-inter">
                  🕒 Availability
                </label>
                <input
                  type="text"
                  value={profile.availability || ''}
                  onChange={(e) => setProfile({...profile, availability: e.target.value})}
                  placeholder="e.g., Evenings, Weekends, Flexible"
                  className="input-glass w-full px-4 py-3 rounded-2xl focus:ring-2 focus:ring-purple-500/50 transition-all duration-300"
                />
              </div>

              <div className="flex gap-4 pt-6">
                <button
                  type="submit"
                  disabled={loading}
                  className="btn-primary flex-1 py-3 px-6 rounded-2xl font-medium font-inter disabled:opacity-50"
                >
                  {loading ? (
                    <div className="flex items-center justify-center">
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                      Saving...
                    </div>
                  ) : (
                    '💾 Save Changes'
                  )}
                </button>
                <button
                  type="button"
                  onClick={() => setEditing(false)}
                  className="btn-secondary flex-1 py-3 px-6 rounded-2xl font-medium font-inter"
                >
                  ❌ Cancel
                </button>
              </div>
            </form>
          ) : (
            /* View Mode */
            <div className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="glass border border-white/10 rounded-2xl p-6">
                  <label className="block text-slate-400 text-sm font-medium mb-2 font-inter">Location</label>
                  <p className="text-lg text-white font-inter flex items-center">
                    📍 {profile.location || 'Not specified'}
                  </p>
                </div>

                <div className="glass border border-white/10 rounded-2xl p-6">
                  <label className="block text-slate-400 text-sm font-medium mb-2 font-inter">Availability</label>
                  <p className="text-lg text-white font-inter flex items-center">
                    🕒 {profile.availability || 'Not specified'}
                  </p>
                </div>
              </div>

              <div className="glass border border-white/10 rounded-2xl p-6">
                <label className="block text-slate-400 text-sm font-medium mb-2 font-inter">Account Status</label>
                <div className="flex items-center space-x-4">
                  <span className="status-accepted px-4 py-2 rounded-full text-sm font-medium">
                    ✅ Active Account
                  </span>
                  <span className="skill-tag px-4 py-2 rounded-full text-sm font-medium">
                    🎯 Ready to Swap
                  </span>
                </div>
              </div>

              <div className="flex gap-4 pt-6">
                <button
                  onClick={() => setEditing(true)}
                  className="btn-primary flex-1 py-3 px-6 rounded-2xl font-medium font-inter"
                >
                  ✏️ Edit Profile
                </button>
                <button
                  onClick={logout}
                  className="btn-secondary flex-1 py-3 px-6 rounded-2xl font-medium font-inter border-red-500/30 text-red-300 hover:bg-red-500/10"
                >
                  🚪 Logout
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Profile Stats */}
        <div className="glass-card rounded-3xl p-8 mt-8 animate-fadeInUp" style={{animationDelay: '0.1s'}}>
          <h3 className="text-lg font-semibold text-white mb-6 font-poppins text-center">Profile Statistics</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-r from-blue-400 to-purple-500 rounded-2xl flex items-center justify-center mx-auto mb-3">
                <span className="text-2xl">👤</span>
              </div>
              <div className="text-xl font-bold text-blue-400 font-poppins">Profile</div>
              <div className="text-slate-400 text-sm font-inter">Complete</div>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-r from-green-400 to-teal-500 rounded-2xl flex items-center justify-center mx-auto mb-3">
                <span className="text-2xl">🎓</span>
              </div>
              <div className="text-xl font-bold text-green-400 font-poppins">Skills</div>
              <div className="text-slate-400 text-sm font-inter">Ready</div>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-r from-purple-400 to-pink-500 rounded-2xl flex items-center justify-center mx-auto mb-3">
                <span className="text-2xl">🔄</span>
              </div>
              <div className="text-xl font-bold text-purple-400 font-poppins">Swaps</div>
              <div className="text-slate-400 text-sm font-inter">Active</div>
            </div>
            <div className="text-center">
              <div className="w-16 h-16 bg-gradient-to-r from-amber-400 to-orange-500 rounded-2xl flex items-center justify-center mx-auto mb-3">
                <span className="text-2xl">⭐</span>
              </div>
              <div className="text-xl font-bold text-amber-400 font-poppins">Rating</div>
              <div className="text-slate-400 text-sm font-inter">Excellent</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
